import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import './App.css';
import type { RootState } from './app/Store';
import { useSelector, useDispatch } from 'react-redux';
import { addItem } from './features/counter/productsSlice';

import ProductInput from './components/ProductInput';
import ProductTypeSelect from './components/ProductTypeSelect';
import SubmitButton from './components/SubmitButton';

interface IState {
  options: {
    value: string
    label: string
  }[]
}

function App() {
  const catergories = [{id: 1, name: 'מוצרי ניקיון'}, {id: 2, name: 'גבינות'}, {id: 3, name: 'ירקות ופירות'},{id: 4, name: 'בשר ודגים'},{id: 5, name: 'מאפים'}];

  // const [products, setProducts] = useState<IState["products"]>([
  //   {category: "מוצרי ניקיון", totalAmount: 1, list: [{name: "סבון כלים", amount: 1}]},
  //   {category: "מוצרי ניקיון", totalAmount: 1, list: [{name: "סבון כלים", amount: 1}]},
  //   {category: "מוצרי ניקיון", totalAmount: 1, list: [{name: "סבון כלים", amount: 1}]}

  // ]);

  const products = useSelector((state: RootState) => state.products)
  const dispatch = useDispatch()

  const [options, setOptions] = useState<IState["options"]>([]);

  console.log(products);



  useEffect(() => {
    const optionsList = catergories.map((item) => { return {value: item.name, label: item.name}});
    setOptions(optionsList);
  }, []);

  // const renderList = (): JSX.Element[] => {
  //   return products..map((product) => {
  //     return (
  //       <div className='list-item'>
  //         <span className='category-title'>{product.category} - {product.totalAmount} מוצרים</span>
  //         <ul className='products-list'>
  //           {product.list.map((item, index) => {
  //             return (
  //               <li key={index}>{item.name}{item.amount > 1 ? <> - {item.amount}</> : <></> }</li>
  //             )
  //           })}
  //         </ul>
  //       </div>
  //     )
  //   });
  // }

  return (
    <div className="App">
      <FormContainer>
        <h1>רשימת קניות</h1>
        {/* <span>סה״כ: {products.length} מוצרים</span> */}
        <Form>
          <ProductInput/>
          <ProductTypeSelect options={options}/>
          <SubmitButton text={"הוסף"}/>
        </Form>
      </FormContainer>
      <ListContainer>
        <h3>יש לאסוף מוצרים אלו במחלקות המתאימות</h3>
        {/* <List>{renderList()}</List> */}
        <SubmitButton text={"שלח הזמנה"}/>
      </ListContainer>
    </div>
  );
}

export default App;

const FormContainer = styled.div`
  width: 100%;  
  padding-block: 10px 25px;
  border-bottom: 1px solid rgba(0,0,0,0.1);
`;

const Form = styled.form`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 15px;
`;

const ListContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 15px;
`;

  const List = styled.div`
  padding-inline: 35px;
  direction: rtl;
  display: grid;
  align-items: center;
  grid-template-columns: repeat(3, 1fr);
  gap: 85px;

  @media(max-width: 1050px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media(max-width: 650px) {
    grid-template-columns: 1fr;
  }


  .list-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }

  .category-title {
    font-size: 24px;
    font-weight: 600;
    letter-spacing: .5px;
  }

  .products-list {
    list-style: none;
  }
`;
